mapper.select:
SELECT 

FROM course_class a

WHERE a.id = #{id}

SELECT 

FROM course_class a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.course_id = #{courseId}


		AND a.course_class = #{courseClass}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM course_class a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE course_class SET 	
	course_id = #{courseId},
	course_class = #{courseClass},
	tips = #{tips},
	remarks = #{remarks}
WHERE id = #{id}

UPDATE course_class SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
