mapper.select:
SELECT 

FROM course a

WHERE a.id = #{id}

SELECT 

FROM course a


	1=1 and
	a.del_flag = #{DEL_FLAG_NORMAL}


		and a.teacher_number=#{teacher.no}



		and a.curs_name=#{cursName}



		and a.curs_term =#{cursTerm}



		and a.curs_num=#{cursNum}

SELECT 

FROM course a


1=1 and
	a.del_flag = #{DEL_FLAG_NORMAL}

	and a.teacher_number=#{teacher.no}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM course a


1=1 and
	a.del_flag = #{DEL_FLAG_NORMAL}
	and sc_lock='2' and right(course_class,8)=#{studentNumber}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM course a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE course SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
