mapper.select:
SELECT 

FROM course_schedule a

WHERE a.id = #{id}

SELECT 

FROM course_schedule a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.time_add = #{timeAdd}


		AND a.course_id = #{courseId}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM course_schedule a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE course_schedule SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
