mapper.select:
SELECT 

FROM uc_dorm a

WHERE a.id = #{id}

SELECT 

FROM uc_dorm a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.dormbuild_id = #{ucDormBuild.id}


		AND a.dorm_number = #{dormNumber}


		AND a.dorm_floor = #{dormFloor}


		AND a.cnt = #{cnt}


		AND a.total = #{total}


		AND a.master = #{master}


		AND a.a = #{a}


		AND a.b = #{b}


		AND a.c = #{c}


		AND a.d = #{d}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM uc_dorm a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE uc_dorm SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
