mapper.select:
SELECT 

FROM chat_group a

WHERE a.id = #{id}

SELECT 

FROM chat_group a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.group_type = #{groupType}


		AND a.online = #{online}


		AND a.avatar = #{avatar}


		AND a.groupname = #{groupname}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM chat_group a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE chat_group SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
