mapper.select:
SELECT 

FROM rs_student_config a

WHERE a.id = #{id}

SELECT 

FROM rs_student_config a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.year_term = #{yearTerm}


		AND a.tip = #{tip}




		ORDER BY ${page.orderBy}

SELECT 

FROM rs_student_config a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE rs_student_config SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
