mapper.select:
SELECT 

FROM rs_jcd a

WHERE a.id = #{id}

SELECT 

FROM rs_jcd a

WHERE a.ksh = #{ksh}

SELECT 

FROM rs_jcd a


	a.del_flag = #{DEL_FLAG_NORMAL}


		AND a.ksh = #{ksh}



		AND a.xm like CONCAT('%',#{xm},'%')



		AND a.sfzh = #{sfzh}



		AND a.zytj = #{zytj}



		AND a.status != 1



		AND a.status = #{status}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM rs_jcd a


	a.del_flag = #{DEL_FLAG_NORMAL}


		AND a.status != 1



		AND a.status = #{status}





		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE rs_jcd SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
