mapper.select:
SELECT 

FROM rs_student a

WHERE a.id = #{id}

SELECT 

FROM rs_student a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.hc_form_xm = #{hc_form_xm}


		AND a.hc_form_xb = #{hc_form_xb}


		AND a.hc_form_sfzh = #{hc_form_sfzh}


		AND a.hc_form_zy1 = #{hc_form_zy1}


		AND a.hc_form_zy2 = #{hc_form_zy2}


		AND a.hc_form_zy3 = #{hc_form_zy3}


		AND a.hc_form_fzrdh = #{hc_form_fzrdh}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM rs_student a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE rs_student SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
