mapper.select:
		SELECT 

		FROM select_course a

		WHERE 
		1=1

        and a.id = #{id}


        and a.course_id = #{course.id}



        and u.no = #{student.no}

SELECT count(a.*) FROM select_course a
WHERE 1=1
and a.id = #{id}
and a.course_id = #{course.id}

		SELECT 

		FROM select_course a


			a.del_flag = #{DEL_FLAG_NORMAL}


        and a.course_id = #{course.id}



       	and u.no = #{student.no}




				ORDER BY ${page.orderBy}


				ORDER BY a.update_date DESC

SELECT 

FROM select_course a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

select @rownum := @rownum + 1 AS id, c.id AS "course.id", c.curs_num AS "course.cursNum", c.curs_edu_num AS "course.cursEduNum", c.curs_name AS "course.cursName", dept.id AS "dept.id", dept.name AS "dept.name", major.id AS "major.id", major.name AS "major.name", cla.id AS "cla.id", cla.name AS "cla.name", b.no as "user.no", b.login_name as "user.loginName", b.phone as "user.phone", b.name as "user.name" from ( SELECT @rownum := 0 ) r, select_course a LEFT JOIN sys_user b on a.student_number=b.no LEFT JOIN sys_office dept on b.company_id=dept.id LEFT JOIN sys_office major on b.office_id=major.id LEFT JOIN sys_office cla on b.clazz_id=cla.id LEFT JOIN course c on a.course_id=c.id LEFT JOIN student_course sc on sc.course_id=c.id
a.del_flag = 0
AND c.id=#{course.id}
AND c.curs_name=#{course.cursName}
AND dept.name=#{dept.name}
AND major.name=#{major.name}
AND cla.name=#{cla.name}
AND b.name=#{user.name}
AND b.phone=#{user.phone}
