mapper.select:
SELECT 

FROM sys_appconfig a

WHERE a.id = #{id}

SELECT 

FROM sys_appconfig a

WHERE a.appid = #{appid}

SELECT 

FROM sys_appconfig a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.appid = #{appid}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.phone = #{phone}


		AND a.status = #{status}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM sys_appconfig a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE sys_appconfig SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
