mapper.select:
SELECT 

FROM sys_config a

WHERE a.id = #{id}

SELECT 

FROM sys_config a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND module=#{module}


		AND status=#{status}

SELECT
FROM sys_config a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.title LIKE '%'||#{title}||'%' '%'+#{title}+'%' concat('%',#{title},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM sys_config a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE sys_config SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
