SELECT 

FROM uc_student a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.student_number = #{studentNumber}


		AND a.username = #{username}


		AND a.id_card = #{idCard}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM uc_student a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
